CREATE COLUMN TABLE "DBADMIN"."APPROVAL" ("ISGROUP" NVARCHAR(50) NOT NULL , "APPROVER" INTEGER NOT NULL , "INVOICE_NO" INTEGER NOT NULL , "PRE_APPROVAL" NVARCHAR(50), "ISAPPROVED" NVARCHAR(50), "APPROVAL_LEVEL" INTEGER, "APPROVAL_TYPE" NVARCHAR(50), "ESCALATE_DATE" DATE, "RULE_ID" INTEGER, "ENTRY_DATE" LONGDATE DEFAULT CURRENT_TIMESTAMP, "END_DATE" LONGDATE DEFAULT CURRENT_TIMESTAMP, "WORKING_PERSON" INTEGER, "TIME_TAKEN" INTEGER, "REFER_LOCK" NVARCHAR(50) DEFAULT 'n', "REFER_COMPLETED" NVARCHAR(50), "REFERRED_APPROVER" NVARCHAR(50) DEFAULT 'n', "ACCEPT_COMMENT" NVARCHAR(300), PRIMARY KEY INVERTED INDIVIDUAL("ISGROUP", "APPROVER", "INVOICE_NO")) UNLOAD PRIORITY 5  AUTO MERGE 